import streamlit as st 

from PIL import Image
im = Image.open('shrdc_logo.png')
st.image(im, width=300)


tab1, tab2, tab3 = st.tabs(["Cat", "Dog", "Owl"])

with tab1:
    st.write("hello world")
    st.header("Hello friends")
    st.subheader("Hello friends")
    st.caption("Hello friends")
    st.header("A cat")
    st.image("data:image/jpeg;base64,", width=200)
with tab2:
    
    new_title = '<p style="font-family:times-new-roman; color:Green; font-size: 42px;">This is advanced font manipulation!</p>'
    st.markdown(new_title, unsafe_allow_html=True)
    st.selectbox("Kuala Lumpur is located at",
    ['Malaysia', 'Thailand', 'UK'])
    st.multiselect("Select 2 states",
    ['Selangor','Johor','Kedah'])
    st.header("A dog")
    st.image("https://static.streamlit.io/examples/dog.jpg", width=200)

with tab3:
    import pandas as pd
    df = pd.read_excel('sampledata.xlsx')
    st.dataframe(df)
    st.bar_chart(df, x="Location", y="Income")
    st.line_chart(df, x="Household", y="Income")
    st.scatter_chart(df, x="Household", y="Income")
    st.header("An owl")
    st.image("https://static.streamlit.io/examples/owl.jpg", width=200)

col1, col2, col3 = st.columns(3)

with col1:
    st.header("A cat")
    st.image("https://static.streamlit.io/examples/cat.jpg")

with col2:
    st.header("A dog")
    st.image("https://static.streamlit.io/examples/dog.jpg")

with col3:
    st.header("An owl")
    st.image("https://static.streamlit.io/examples/owl.jpg")

